TEST(PROCESSXCONFCONFIGURATION, TEST_NULL_INVALID)
{
     ProfileXConf *profile = 0;
     fstream new_file;
     char* data = NULL;
     new_file.open("xconfInputfile.txt", ios::in);

     if (new_file.is_open()) {
        string sa;
// 1st case schedule NULL
	getline(new_file, sa);
	cout << sa << endl;
	int len = sa.length();
        data = new char[len + 1];
	strcpy(data, sa.c_str());
	cout << data << endl;
        EXPECT_EQ(T2ERROR_FAILURE, processConfigurationXConf(data, &profile));
        delete[] data;
// 2nd case ProfileName NULL	
        getline(new_file, sa);

	cout << sa << endl;
        len = sa.length();
        data = new char[len + 1];
        strcpy(data, sa.c_str());
        cout << data << endl;
	EXPECT_EQ(T2ERROR_FAILURE, processConfigurationXConf(data, &profile));
	delete[] data;
// 3rd case URL NULL
	getline(new_file, sa);

        cout << sa << endl;
        len = sa.length();
        data = new char[len + 1];
        strcpy(data, sa.c_str());
        cout << data << endl;
        EXPECT_EQ(T2ERROR_FAILURE, processConfigurationXConf(data, &profile));
	delete[] data;
// 4th case Profiledata NULL
	getline(new_file, sa);

        cout << sa << endl;
        len = sa.length();
        data = new char[len + 1];
        strcpy(data, sa.c_str());
        cout << data << endl;
        EXPECT_EQ(T2ERROR_FAILURE, processConfigurationXConf(data, &profile));
	delete[] data;

// 5th case Working case
        getline(new_file, sa);

        cout << sa << endl;
        len = sa.length();
        data = new char[len + 1];
        strcpy(data, sa.c_str());
        cout << data << endl;
        EXPECT_EQ(T2ERROR_SUCCESS, processConfigurationXConf(data, &profile));
        delete[] data;
     }
     new_file.close();
}

